                }
            ]
        },
        {
            key: "2",
            label: "Facial Expression",
            children: [
                {
                    key: "2a",
                    label: "Happy",
                },
                {
                    key: "2b",
                    label: "Sad",
                },
                {
                    key: "2c",
                    label: "Angry",
                },
                {
                    key: "2d",
                    label: "Surprise",
                },
                {
                    key: "2e",
                    label: "Disgust",
                }, 
                {
                    key: "2f",
                    label: "Neutral",
                }
            ]
        },
        {
            key: "3",
            label: "Eye Movement",
            children: [
                {
                    key: "3a",
                    label: "Left",
                },
                {
                    key: "3b",
                    label: "Right",
                },
                {
                    key: "3c",
                    label: "Up",
                },
                {
                    key: "3d",
                    label: "Down",
                }
            ]
        },
        {
            key: "4",
            label: "Gender Customization",
            children: [
                {
                    key: "4a",
                    label: "Male",
                },
                {
                    key: "4b",
                    label: "Female",
                }
            ]
        },
        {
            key: "5",
            label: "Face Aging",
            children: [
                {
                    key: "5a",
                    label: "Older",
                },
                {
                    key: "5b",
                    label: "Younger",
                }
            ]
        },
        {
            key: "6",
            label: "Anime-Style",
            children: [
                {
                    key: "6a",
                    label: "Anime",
                }
            ]
        },
        {
            key: "7",
            label: "Produce Audio",
            children: [
                {
                    key: "7a",
                    label: "Audio Clip 1",
                },
                {
                    key: "7b", 
                    label: "Audio Clip 2",
                },
                {
                    key: "7c",
                    label: "Audio Clip 3",
                }
            ]
        }
    ]
}];

function FeatureSelection(features) {
    return features.children.map((feature) => {
        return { feature, features: feature.children && FeatureSelection(feature.children)}
    });
}

class App extends React.Component {
    state = { data: FeatureSelection(data)}

    render() {
        return (
            <TreeView data={this.state.data} textField='feature.label' aria-multiselectable={false} onItemClick={this.onItemClick} ExpandMoreIcon={true} onExpandChange={this.onExpandChange}/>
        )
    }

    onItemClick = (event) => {
        event.item.selected = !event.item.selected;
        this.forceUpdate();
    }

    onExpandChange = (event) => {
        event.item.expanded = !event.item.expanded;
        this.forceUpdate();
    }
}


