import React, { useState } from 'react'
import { useDropzone } from 'react-dropzone'
import './ImageUploader.css';

function ImageUploader() {
    const [image, setImage] = useState([]);

    const {getRootProps, getInputProps, isDragActive} = useDropzone({
        accept: "image/jpg, image/png, image/jpeg",
        onDrop: (acceptedFiles) => {
            setImage (
                acceptedFiles.map((upFile) => Object.assign(upFile, {
                    preview: URL.createObjectURL(upFile)
                }))
            )
        }
    })

    const uploadImage = async () => {
        const requestOptions = {
          method: "POST",
          headers: {
            "Content-Type": "multipart/form-data",
          },
          body: JSON.stringify(
            `file=@${image};type=image/jpeg`
          ),
        };
    
        const response = await fetch("/get-image", requestOptions);
        const data = await response.json();

        if (!response.ok) {
            console.log("Error uploading")
          } else {
            console.log("Successful upload")
        }
      };

    return (
        <div className="image-uploader">
            <header className="image-header">
                <div {...getRootProps()} style={{border: "3px solid black", height:"300px", width:"250px", textAlign: "center", display: "flex", justifyContent: "center", alignItems: "center"}}>
                    <input {...getInputProps()} />
                    {
                        isDragActive ? <p className="imageBoxHover">Drop Image Here<br/><br/><p style={{fontSize:12}}>(*/File type: .jpg/.jpeg/.png only*/)</p></p> : <p className="imageBox">Drag n' Drop Image<br/>OR<br/><u>Browse Image</u><br/><br/><p style={{fontSize:12}}>(*/File type: .jpg/.jpeg/.png only*/)</p></p>
                    }
                </div>
                <div onChange={uploadImage}>
                        return (
                            <div>
                                <img src={image.preview} alt="Preview" style={{width:"250", height:"300px", border:"3px solid #fff"}} />
                            </div>
                        )
                </div>
            </header>
        </div>
    )
}

export default ImageUploader;
