import React, { useState } from 'react'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const FeatureList = ({ data = [] }) => {
  return (
    <div className="d-tree">
        <ul className="d-flex d-tree-container flex-column">
            {data.map(tree => (
                <Features feature={tree} />
            ))}    
        </ul>
    </div>
  )
}

const Features = ({feature}) => {
  const [childVisible, setChildVisibility] = useState(false);

  const hasChild = feature.children ? true : false;

  return (
        <li className="d-tree-node border-0">
            <div className="d-flex" onClick={e => setChildVisibility(v => !v)}>
                {hasChild && (
                    <div className={`d-inline d-tree-toggler ${ childVisible ? "active" : "" }`}>
                        <i class="fa-solid fa-angle-right"></i>
                    </div>
                )}  

                <div className="col d-tree-head">
                    <i className={`mr-1 ${feature.icon}`}></i>
                    {feature.label}
                </div>  
            </div>

            {
                hasChild && childVisible && <div className="d-tree-content">
                    <ul className="d-flex d-tree-container flex-column">
                        <FeatureList data={feature.children} />
                    </ul>
                    </div>
            }
        </li>
  )

}

export default FeatureList
