import { TreeView } from '@progress/kendo-react-treeview'
import './FeatureSelection.css'

const data = {
    key: "feature",
    label: "Features",
    children: 
    [
        {
            key: "1",
            label: "Head Movement",
            children: [
                {
                    key: "1a",
                    label: "Left",
                },
                {
                    key: "1b",
                    label: "Right",
                },
                {
                    key: "1c",
                    label: "Up",
                },
                {
                    key: "1d",
                    label: "Down",
                }
            ]
        },
        {
            key: "2",
            label: "Facial Expression",
            children: [
                {
                    key: "2a",
                    label: "Happy",
                },
                {
                    key: "2b",
                    label: "Sad",
                },
                {
                    key: "2c",
                    label: "Angry",
                },
                {
                    key: "2d",
                    label: "Surprise",
                },
                {
                    key: "2e",
                    label: "Disgust",
                }, 
                {
                    key: "2f",
                    label: "Neutral",
                }
            ]
        },
        {
            key: "3",
            label: "Eye Movement",
            children: [
                {
                    key: "3a",
                    label: "Left",
                },
                {
                    key: "3b",
                    label: "Right",
                },
                {
                    key: "3c",
                    label: "Up",
                },
                {
                    key: "3d",
                    label: "Down",
                }
            ]
        },
        {
            key: "4",
            label: "Gender Customization",
            children: [
                {
                    key: "4a",
                    label: "Male",
                },
                {
                    key: "4b",
                    label: "Female",
                }
            ]
        },
        {
            key: "5",
            label: "Face Aging",
            children: [
                {
                    key: "5a",
                    label: "Older",
                },
                {
                    key: "5b",
                    label: "Younger",
                }
            ]
        },
        {
            key: "6",
            label: "Anime-Style",
            children: [
                {
                    key: "6a",
                    label: "Anime",
                }
            ]
        },
        {
            key: "7",
            label: "Produce Audio",
            children: [
                {
                    key: "7a",
                    label: "Audio Clip 1",
                },
                {
                    key: "7b", 
                    label: "Audio Clip 2",
                },
                {
                    key: "7c",
                    label: "Audio Clip 3",
                }
            ]
        }
    ]
};

function FeatureSelection(features) {
    const [ open, setOpen ] = useState();

    return (
        <li className="feature-item">
            <a href="#" className="eachFeature" onClick={() => setOpen(!open)}>
                <span className='icon-button'>{features.label}</span>
                {features.label}
                <span className='icon-right'>{features.label}</span>
            </a>

            {open && features.children}
        </li>
    )
}


