import React, { useState, useContext } from 'react'
import { useDropzone } from 'react-dropzone'
import './ImageUploader.css';
import ImageContext from './ImageContext';

function ImageUploader() {
    // const { image, setImage } = useContext(ImageContext);

    const [image, setImage] = useState([]);

    const {getRootProps, getInputProps, isDragActive, isDragReject, acceptedFiles, open} = useDropzone({
        accept: "image/jpg, image/png, image/jpeg",
        noKeyboard: true,
        onDrop: acceptedFiles => {
            setImage(acceptedFiles.map(file => Object.assign(file, {
                preview: URL.createObjectURL(file)
            })))
        }
    })

    {/*const uploadImage = (e) => {
        const requestOptions = {
          method: "POST",
          headers: {
            "Content-Type": "multipart/form-data",
          },
          body: JSON.stringify(
            `file=@${image};type=image/jpeg`
          ),
        };
    
        const response = await fetch("/get-image", requestOptions);
        const data = await response.json();

        if (!response.ok) {
            console.log("Error uploading")
          } else {
            console.log("Successful upload")
        }
    };*/}

    return (
        <div className="image-uploader">
            <header className="image-header">
                <div {...getRootProps()} style={{border: "3px solid black", height:"300px", width:"250px", textAlign: "center", display: "flex", justifyContent: "center", alignItems: "center"}}>
                    <label {...getRootProps()}>
                        <input {...getInputProps({className: "imageUploaded"})}/>
                        {
                            isDragActive ? <div className="imageBoxHover">Drop Image Here<br/><br/><p style={{fontSize:12}}>(*/File type: .jpg/.jpeg/.png only*/)</p></div> : <div className="imageBox">Drag n' Drop Image<br/>OR<br/><u>Browse Image</u><br/><br/><p style={{fontSize:12}}>(*/File type: .jpg/.jpeg/.png only*/)</p></div>
                        }
                    </label>
                </div>
                <div /*onChange={uploadImage}*/>
                    {image.map((file) => {
                        return (
                            <div>
                                <img src={file.preview} alt="Preview" style={{width:"250", height:"300px", border:"3px solid #fff"}} />
                            </div>
                        )
                    })}
                </div>
            </header>
        </div>
    )
}

export default ImageUploader;
