function FeatureSelection() {
    const [ selectedFeature, setSelectedFeature ] = useState(null)

    const [image, setImage] = useState([]);

    const {getRootProps, getInputProps, isDragActive, isDragReject, acceptedFiles, open} = useDropzone({
        accept: "image/jpg, image/png, image/jpeg",
        noKeyboard: true,
        onDrop: acceptedFiles => {
            setImage(acceptedFiles.map(file => Object.assign(file, {
                preview: URL.createObjectURL(file)
            })))
        }
    })

    {/*const uploadImage = (e) => {
        const requestOptions = {
          method: "POST",
          headers: {
            "Content-Type": "multipart/form-data",
          },
          body: JSON.stringify(
            `file=@${image};type=image/jpeg`
          ),
        };
    
        const response = await fetch("/get-image", requestOptions);
        const data = await response.json();

        if (!response.ok) {
            console.log("Error uploading")
          } else {
            console.log("Successful upload")
        }
    };*/}
    

    {/*const twoCharRegex = /^[1-9][a-zA-Z]+$/;*/}

    {/*This is accepting range from 1 - 100*/}
    const regex = /^[0-9\b]+$/;

    useEffect(() => {
        console.log("Feature selected: ", selectedFeature)
    })

    const NodeSelect = (event, nodeId) => {
        if (regex.test(nodeId)) {
            setSelectedFeature(nodeId)
        } else {
            setSelectedFeature(null)
        }
    }

    const transformImage = async () => {

        const fD = new FormData()

        fD.append(
            "file",
            image,
            image.name
        )

        if (selectedFeature != null && image != null) {
            const requestOptions = {
            method: 'POST',
            body: fD
            }

            fetch("/get-image", requestOptions)
                .then(response => response.json())
                .then(function (response) {
                    console.log('response')
                    console.log(response)
            });
        } else {
            console.log("No feature selected or no image uploaded")
        }
    }

    /*const transformImage = async () => {
        let imageUpload = document.querySelector(".imageUploaded")
        
        console.log(imageUpload)
        let fD = new FormData()
        // fD.append("featureID", selectedFeature)
        fD.append("file", imageUpload)

        axios.post("/get-image", fD, {'Content-Type': 'multipart/form-data'})
            .then(response => {
                console.log("REsponse",response)
            }) .catch ((error) => {
                console.log("Error",error)
            })
    }*/

    const renderTree = (features) => (
        <TreeItem key={features.key} nodeId={features.key} label={features.label}>
            {Array.isArray(features.children) ? features.children.map((feature) => renderTree(feature)) : null}
        </TreeItem>
    )

    return (
        <div>
<div className="image-uploader">
            <header className="image-header">
                <div {...getRootProps()} style={{border: "3px solid black", height:"300px", width:"250px", textAlign: "center", display: "flex", justifyContent: "center", alignItems: "center"}}>
                    <label {...getRootProps()}>
                        <input {...getInputProps({className: "imageUploaded"})}/>
                        {
                            isDragActive ? <div className="imageBoxHover">Drop Image Here<br/><br/><p style={{fontSize:12}}>(*/File type: .jpg/.jpeg/.png only*/)</p></div> : <div className="imageBox">Drag n' Drop Image<br/>OR<br/><u>Browse Image</u><br/><br/><p style={{fontSize:12}}>(*/File type: .jpg/.jpeg/.png only*/)</p></div>
                        }
                    </label>
                </div>
                <div /*onChange={uploadImage}*/>
                    {image.map((file) => {
                        return (
                            <div>
                                <img src={file.preview} alt="Preview" style={{width:"250", height:"300px", border:"3px solid #fff"}} />
                            </div>
                        )
                    })}
                </div>
            </header>
        </div>
        <div style={{marginLeft: "auto", marginRight: "auto", width: "300px"}}>
                <TreeView defaultCollapseIcon={<ExpandMoreIcon />} defaultExpanded={["feature"]} defaultExpandIcon={<ChevronRightIcon />} sx={{height: 110, flexGrow: 1}} onNodeSelect={NodeSelect}>
                    {renderTree(data)}
                    <button className='transformImage' onClick={transformImage}>Transform!</button>
                </TreeView>
        </div>
        </div>
    )
}

export default FeatureSelection;
