import {
  Center,
  Text,
  Heading,
  VStack,
  Button,
  Input,
  HStack,
  Container,
  SimpleGrid,
  Box,
  Image,
  Spinner,
} from "@chakra-ui/react";

import { ChakraProvider } from "@chakra-ui/react";
import React, { useEffect, useState, useContext } from "react";
import TreeView from "@mui/lab/TreeView";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import TreeItem from "@mui/lab/TreeItem";
import "./ImageAndFeature.css";

const data = {
  key: "feature",
  label: "Features",
  children: [
    {
      key: "1",
      label: "Head Movement",
    },
    {
      key: "2",
      label: "Facial Expression",
    },
    {
      key: "3",
      label: "Eye Movement",
    },
    {
      key: "GC",
      label: "Gender Customization",
      children: [
        {
          key: "4",
          label: "Male",
        },
        {
          key: "5",
          label: "Female",
        },
      ],
    },
    {
      key: "FA",
      label: "Face Aging",
      children: [
        {
          key: "6",
          label: "Older",
        },
        {
          key: "7",
          label: "Younger",
        },
      ],
    },
    {
      key: "8",
      label: "Anime-Style",
    },
    {
      key: "PA",
      label: "Produce Audio",
      children: [
        {
          key: "9",
          label: "Woman - Hello",
        },
        {
          key: "10",
          label: "Woman - Bye",
        },
        {
          key: "11",
          label: "Man - Hello",
        },
        {
          key: "12",
          label: "Man - Bye",
        },
        {
          key: "13",
          label: "Cartoon - Hello",
        },
        {
          key: "14",
          label: "Cartoon - Bye",
        },
      ],
    },
  ],
};

function ImageAndFeature() {
  const [selectedFeature, setSelectedFeature] = useState(null);
  const [isSelected, setIsSelected] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadSuccessful, setUploadSuccessful] = useState(false);
  const [showSpinner, setShowSpinner] = useState(false);
  const [allPhotos, setAllPhotos] = useState([]);
  const regex = /^[0-9\b]+$/;

  const NodeSelect = (event, nodeId) => {
    if (regex.test(nodeId)) {
      setSelectedFeature(nodeId);
    } else {
      setSelectedFeature(null);
    }
  };

  const transformImage = async () => {
    setSelectedFeature(selectedFeature);
    setShowSpinner(true);
    const formData = new FormData();
    formData.append("file", selectedFile, selectedFile.name);
    fetch("/get-image", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Success posting!!");
        setUploadSuccessful(!uploadSuccessful);
        setShowSpinner(false);
      });
  };

  const renderTree = (features) => (
    <TreeItem key={features.key} nodeId={features.key} label={features.label}>
      {Array.isArray(features.children)
        ? features.children.map((feature) => renderTree(feature))
        : null}
    </TreeItem>
  );

  useEffect(() => {
    console.log("Feature selected: ", selectedFeature);

    fetch("/get-image")
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setAllPhotos(data);
      });
  }, [uploadSuccessful]);

  const onInputChange = (e) => {
    console.log(e.target.files[0]);
    setIsSelected(true);

    setSelectedFile(e.target.files[0]);
  };

  const onButtonClick = (e) => {
    console.log("Button clicked..");
    e.target.value = "";
  };

  const onFileUpload = (e) => {
    setShowSpinner(true);
    const formData = new FormData();
    formData.append("featureID", selectedFeature)
    formData.append("file", selectedFile, selectedFile.name);
    fetch("/get-image", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Success posting!!");
        setUploadSuccessful(!uploadSuccessful);
        setShowSpinner(false);
      });
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "rgb(28, 27, 27)",
      }}
    >
      <div style={{ width: "30px" }}></div>
      <div className="Contents">
        <ChakraProvider>
          <Center bg="rgb(28, 27, 27)" color="white" padding={8} width="450px" height="500px">
            <VStack spacing={7}>
              <Heading>Your Gallery</Heading>
              <Text>Take a look at all your photos!</Text>
              <HStack>
                <input style={{marginLeft: "80px"}}
                  type="file"
                  onChange={onInputChange}
                  onClick={onButtonClick}
                ></input>
                {showSpinner && (
                  <Center>
                    <Spinner size="xl" />
                  </Center>
                )}
              </HStack>
              <Heading>Preview</Heading>
              <SimpleGrid columns={3} spacing={8}>
                {/*{selectedFile.map(file => Object.assign(file, {preview: URL.createObjectURL(file)}))}*/}
              </SimpleGrid>
            </VStack>
          </Center>
        </ChakraProvider>
        <div style={{ width: "76px" }}></div>
        <div
          style={{
            backgroundColor: "white",
            color: "rgb(28, 27, 27)",
            marginLeft: "auto",
            marginRight: "auto",
            width: "450px",
            height: "500px",
          }}
        >
          <TreeView
            defaultCollapseIcon={<ExpandMoreIcon />}
            defaultExpanded={["feature"]}
            defaultExpandIcon={<ChevronRightIcon />}
            sx={{ height: 110, flexGrow: 1 }}
            onNodeSelect={NodeSelect}
          >
            {renderTree(data)}
            <div style={{height: "60px"}}></div>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <button
                onClick={onFileUpload}
              >
                Transform!
              </button>
            </div>
          </TreeView>
        </div>
      </div>
      <div style={{ width: "200px" }}></div>
      <div
        className="output-area"
        style={{ backgroundColor: "white", color: "rgb(28, 27, 27)" }}
      >
        <div
          style={{
            border: "3px solid black",
            height: "500px",
            width: "450px",
            textAlign: "center",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          {/*<p className="outputBox">Output area</p>*/}
          <img src={selectedFile} alt="Test" />
        </div>
      </div>
    </div>
  );
}

export default ImageAndFeature;
