import React, { useState, useRef, useEffect } from 'react'
import './LoginPage.css'
import { Grid, Paper, Avatar, TextField, Button } from '@material-ui/core'
import VpnKeyOutlinedIcon from '@mui/icons-material/VpnKeyOutlined';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from "../Home";
import { useForm } from "react-hook-form";

const  LoginPage = () => {
  const userRef = useRef();
  const errRef = useRef();

  const [ user, setUser ] = useState('')
  const [ pw, setPw ] = useState('')
  const [ errMsg, setErrMsg ] = useState('')
  const [ isLoggedIn, setLoggedIn ] = useState(false)

  useEffect(() => {
    userRef.current.focus();
  }, [])

  useEffect(() => {
    setErrMsg('');
  }, [user, pw])

  const { register, handleSubmit, formState: {errors} } = useForm();
  const onSubmit = (data) => console.log(data)
  console.log(errors);

  return (
    <Grid>
      <Paper className='paperStyle' elevation={10}>
        <h2>Face Mover&nbsp;&nbsp;<i className="fas fa-code-branch fas-fade"></i></h2>
        <Grid className='introSign' align="center">
          <Avatar id="loginLogo"><VpnKeyOutlinedIcon/></Avatar>
          <h3 className='space'>Sign in</h3>
        </Grid>
        <form onSubmit={handleSubmit(onSubmit)}>
          <TextField id="userField" label="Username" name="username" placeholder="Enter your username" variant="standard" {...register("username", {required: "Username required"})} error={Boolean(errors.username)} helperText={errors.username?.message} fullWidth/>
          <TextField id="passwordField" label="Password" name="password" placeholder="Enter your password" variant="standard" type="password" {...register("password", {required: "Password required"})} error={Boolean(errors.password)} helperText={errors.password?.message} fullWidth/>
          {/*<FormControlLabel
              control={
                <Checkbox
                  name="checkedB"
                  color="primary"
                /> 
              }
            label="Remember me"/>*/}
          <Button id="submitButton" type="submit" color="primary" variant="contained" fullWidth>Sign In</Button>
        </form>
      </Paper>
    </Grid>
  )
}

export default LoginPage
