import React, { useState, /*useEffect*/ useContext } from "react";
 import { Link } from "react-router-dom";
import LoginContext from "../Login/LoginContext";
import "./Navbar.css";
import { Anchor } from 'antd'

//const { Link } = Anchor;

function Navbar() {
  const { setToken } = useContext(LoginContext)
  const [click, setClick] = useState(false);
  // const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);

  const closeMobileMenu = () => setClick(false);

  const handleLogout = () => {
    setToken(null);
  }

  /* const showButton = () => {
        if(window.innerWidth <= 960) {
            setButton(false);
        } else {
            setButton(true);
        }
    };

    useEffect(() => {
        showButton()
    }, []);

    window.addEventListener('resize', showButton); */

  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
            <b>Face Mover</b>&nbsp;&nbsp;
            <i className="fas fa-code-branch fas-fade"></i>
          </Link>
          <div className="menu-icon" onClick={handleClick}>
            <i className={click ? "fas fa-times" : "fas fa-bars"} />
          </div>
          <ul className={click ? "nav-menu active" : "nav-menu"}>
            <li className="nav-item" style={{ color: "white" }}>
              Welcome {}
            </li>
            <li className="nav-item">
              <Link to="/" className="nav-links" onClick={closeMobileMenu}>
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="/animator"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                Animator
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="/details"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                Project Details
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="/aboutus"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                About us
              </Link>
            </li>
            <li className="nav-item">
              <div className="nav-links" onClick={handleLogout}>
                Logout
              </div>
            </li>
          </ul>
        </div>
      </nav>
  </>
  // <nav className="navbar">
  //       <div className="navbar-container">
  //         <Anchor>
  //           <Link href="#hero" className="navbar-logo" onClick={closeMobileMenu}>
  //            <b>Face Mover</b>&nbsp;&nbsp;
  //            <i className="fas fa-code-branch fas-fade"></i>
  //           </Link>
  //           <div className="menu-icon" onClick={handleClick}>
  //            <i className={click ? "fas fa-times" : "fas fa-bars"} />
  //          </div>
  //         <ul className={click ? "nav-menu active" : "nav-menu"}>
  //            <li className="nav-item" style={{ color: "white" }}>
  //              Welcome {}
  //              </li>
  //            <li className="nav-item">
  //              <Link href="#hero" title="Home" className="nav-links" onClick={closeMobileMenu} />
  //            </li>
  //            <li className="nav-item">
  //             <Link href="#animator" title="Animator" className="nav-links" onClick={closeMobileMenu}/>
  //            </li>
  //           </ul>
            
  //         </Anchor>
  //       </div>
  // </nav>
  );
}

export default Navbar;
