import {
  Center,
  Text,
  Heading,
  VStack,
  HStack,
  SimpleGrid,
  Spinner,
} from "@chakra-ui/react";

import { ChakraProvider } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import TreeView from "@mui/lab/TreeView";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import TreeItem from "@mui/lab/TreeItem";
import "./ImageAndFeature.css";
import axios from "axios";
import ReactPlayer from "react-player";
import testVid from "../../img/bg_vid.mp4";
import VideoPlayer from 'react-video-js-player'
import $ from 'jquery'
import { Player } from 'video-react'

const data = {
  key: "feature",
  label: "Features",
  children: [
    {
      key: "1",
      label: "Facial Expression",
    },
    {
      key: "2",
      label: "Eye Movement",
    },
    {
      key: "GC",
      label: "Gender Customization",
      children: [
        {
          key: "3",
          label: "Female",
        },
        {
          key: "4",
          label: "Male",
        },
      ],
    },
    {
      key: "FA",
      label: "Face Aging",
      children: [
        {
          key: "5",
          label: "Older",
        },
        {
          key: "6",
          label: "Younger",
        },
      ],
    },
    {
      key: "7",
      label: "Anime-Style",
    },
    {
      key: "PA",
      label: "Produce Audio",
      children: [
        {
          key: "8",
          label: "Cartoon - Bye",
        },
        {
          key: "9",
          label: "Cartoon - Hello",
        },
        {
          key: "10",
          label: "Woman - Bye",
        },
        {
          key: "11",
          label: "Woman - Hello",
        },
        {
          key: "12",
          label: "Man - Bye",
        },
        {
          key: "13",
          label: "Man - Hello",
        },
      ],
    },
  ],
};

function ImageAndFeature() {
  const [selectedFeature, setSelectedFeature] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadSuccessful, setUploadSuccessful] = useState(false);
  const [showSpinner, setShowSpinner] = useState(false);
  const [outputImg, setOutputImg] = useState(null);
  const regex = /^[0-9\b]+$/;

  const NodeSelect = (event, nodeId) => {
    if (regex.test(nodeId)) {
      setSelectedFeature(nodeId);
      console.log(nodeId);
    } else {
      setSelectedFeature(null);
      console.log(nodeId);
    }
  };

  const renderTree = (features) => (
    <TreeItem key={features.key} nodeId={features.key} label={features.label}>
      {Array.isArray(features.children)
        ? features.children.map((feature) => renderTree(feature))
        : null}
    </TreeItem>
  );

  // useEffect(() => {
  //   console.log("Feature selected: ", selectedFeature);

  //   const url = `/output-image`;
  //   axios.get(url, { responseType: "blob" }).then((res) => {
  //     var imageUrl = URL.createObjectURL(res.data);
  //     setOutputImg(imageUrl);
  //     console.log("Output image", outputImg)
  //   })
  // }, [uploadSuccessful]);

  useEffect(() => {
    getOutput()
  }, [])

  // async function getOutput() {
  //   await axios.get("/output-image", {headers: {Accept: "video/mp4;charset=UTF-8"}})
  //   .then((response) => {
  //     var imageUrl = (window.URL || window.webkitURL).createObjectURL(new Blob([response.data]));
  //     setOutputImg(imageUrl);
  //     console.log("Output image", outputImg)
  //   })

  //   // fileLength = len(file) - 4
  //   //             file = file[:fileLength] + "#" + ".mp4"
  //   //             print(file)
  // }

  async function getOutput() {
    await axios.get("/output-image", {headers: {Accept: "video/mp4;charset=UTF-8"}})
    .then((response) => {
      // var imageUrl = (window.URL || window.webkitURL).createObjectURL(new Blob([response.data]));
      // setOutputImg(imageUrl);
      $("#img1").attr("src", window.URL.createObjectURL(new Blob([response.data])))
      console.log("Output image", outputImg)
      console.log(response)
    })

    // fileLength = len(file) - 4
    //             file = file[:fileLength] + "#" + ".mp4"
    //             print(file)
  }

  const onInputChange = (e) => {
    console.log(e.target.files[0]);

    setSelectedFile(e.target.files[0]);
  };

  const onButtonClick = (e) => {
    console.log("Button clicked..");
    e.target.value = "";
  };

  const transformImage = (e) => {
    setShowSpinner(true);
    console.log("UploadFeature", selectedFeature);
    const formData = new FormData();
    formData.append("file", selectedFile, selectedFile.name);

    const requestOptions = {
      method: "POST",
      body: formData,
    };

    fetch("/get-image", requestOptions)
      .then((response) => response.json())
      .then((data) => {
        console.log("Image sent");
        setUploadSuccessful(!uploadSuccessful);
        setShowSpinner(false);
        setSelectedFile(null);
    });
  };

  const sendFeatureID = (e) => {
    axios
      .post("/get-feature", {
        feature_id: selectedFeature,
      })
      .then(function (response) {
        console.log("Feature sent");
      });
  };

  // const getOutputImg = setInterval(function () {
  //   const url = `/output-image`;
  //   axios.get(url, { responseType: "blob" }).then((res) => {
  //     var imageUrl = URL.createObjectURL(res.data);
  //     setOutputImg(imageUrl);
  //   });
  // }, 15000);

  return (
    <div className="container">
      {/* <div className="filler1"></div> */}
      <div className="Contents">
        <ul className="animateItem">
          <li className="items">
            <ChakraProvider>
              <Center
                sx={{
                  padding: 8,
                  width: "fit-content",
                  height: "500px",
                  border: "solid white",
                }}
              >
                <VStack spacing={7}>
                  <Heading>Upload your Image</Heading>
                  <Text>/* accepted files (.png, .jpg) */</Text>
                  <HStack>
                    <input
                      style={{ marginLeft: "80px" }}
                      type="file"
                      onChange={onInputChange}
                      onClick={onButtonClick}
                    ></input>
                    {showSpinner && (
                      <Center>
                        <Spinner size="xl" />
                      </Center>
                    )}
                  </HStack>
                  <Heading>Preview</Heading>
                  <SimpleGrid columns={3} spacing={8}>
                    {/*{selectedFile.map(file => Object.assign(file, {preview: URL.createObjectURL(file)}))}*/}
                  </SimpleGrid>
                </VStack>
              </Center>
            </ChakraProvider>
          </li>
          {/* <div className="filler2"></div> */}
          <li className="items">
            <div
              className="inputFeat"
              style={{
                marginLeft: "auto",
                marginRight: "auto",
                width: "450px",
                height: "500px",
                border: "solid white",
              }}
            >
              <TreeView
                defaultCollapseIcon={<ExpandMoreIcon />}
                defaultExpanded={["feature"]}
                defaultExpandIcon={<ChevronRightIcon />}
                sx={{ height: 110, flexGrow: 1, marginTop: 4, marginLeft: 2 }}
                onNodeSelect={NodeSelect}
              >
                {renderTree(data)}
                <div style={{ height: "60px" }}></div>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                  }}
                >
                  <button
                    onClick={function (event) {
                      transformImage();
                      sendFeatureID();
                      // getOutputImg();
                    }}
                  >
                    Transform!
                  </button>
                </div>
              </TreeView>
            </div>
          </li>
            
        </ul>
        <div
              style={{
                height: "500px",
                width: "450px",
                textAlign: "center",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                border: "solid white",
              }}
            >
              {/* <img src={outputImg}  className='outputGif' /> */}
              {/* <img id="img1" /> */}
              <video id="img1" width="720" height="420" controls autoPlay="" muted/>
            </div>
      </div>
    </div>
  );
}

export default ImageAndFeature;
