import React, { useState } from 'react'
import './LoginPage.css'
import { Grid, Paper, Avatar, TextField, Button } from '@material-ui/core'
import VpnKeyOutlinedIcon from '@mui/icons-material/VpnKeyOutlined';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';

const  LoginPage = () => {

  const state = {
    username: "",
    usernameError: "",
    password: "",
    passwordError: ""
  }

  const change = (e) => {
    this.props.onChange({[e.target.name]: e.target.value});
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  const onSubmit = (e) => {
    e.preventDefault();
    const error = checkErrors()
    if(!err) {
      <Router>
        <Routes>
          <Route path='/' element={<LoginPage/>}/>
        </Routes>
      </Router>
    } else {
      this.setState({
        username: "",
        password: ""
      })

      this.props.onChange({
        username: "",
        password: ""
      })
    }
  }

  return (
    <Grid>
      <Paper className='paperStyle' elevation={10}>
        <h2>Face Mover&nbsp;&nbsp;<i className="fas fa-code-branch fas-fade"></i></h2>
        <Grid className='introSign' align="center">
          <Avatar id="loginLogo"><VpnKeyOutlinedIcon/></Avatar>
          <h3 className='space'>Sign in</h3>
        </Grid>
        <TextField id="userField" label="Username" placeholder="Enter your username" variant="standard" errorText={this.state.usernameError} fullWidth required/>
        <TextField id="passwordField" label="Password" placeholder="Enter your password" variant="standard" type="password" errorText={this.state.passwordError} fullWidth required/>
        {/*<FormControlLabel
            control={
              <Checkbox
                name="checkedB"
                color="primary"
              /> 
            }
          label="Remember me"/>*/}
        <Button id="submitButton" type="submit" color="primary" variant="contained" fullWidth>Sign In</Button>
      </Paper>
    </Grid>
  )
}

export default LoginPage
