import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';

const  LoginPage = () => {

  state = {
    username: "",
    usernameError: "",
    password: "",
    passwordError: ""
  }

  return (
    <Grid>
      <Paper className='paperStyle' elevation={10} onSubmit={handleSubmit}>
        <h2>Face Mover&nbsp;&nbsp;<i className="fas fa-code-branch fas-fade"></i></h2>
        <pre>{JSON.stringify(loginDets, undefined, 2)}</pre>
        <Grid className='introSign' align="center">
          <Avatar id="loginLogo"><VpnKeyOutlinedIcon/></Avatar>
          <h3 className='space'>Sign in</h3>
        </Grid>
        <TextField id="userField" label="Username" placeholder="Enter your username" variant="standard" fullWidth required/>
        <TextField id="passwordField" label="Password" placeholder="Enter your password" variant="standard" type="password" fullWidth required/>
        {/*<FormControlLabel
            control={
              <Checkbox
                name="checkedB"
                color="primary"
              /> 
            }
          label="Remember me"/>*/}
        <Button id="submitButton" type="submit" color="primary" variant="contained" fullWidth>Sign In</Button>
      </Paper>
    </Grid>
  )
}

export default LoginPage
