import React, { createContext, useEffect, useState } from "react";
import axios from 'axios';
import { API_URL } from "../../Constants";

export const LoginContext = createContext();

export const LoginContextProvider = (props) => {
  const defaultUser = {
    username: "",
    email: "",
    subTier: "",
    startDate: "",
    endDate: "",
  };

  const [token, setToken] = useState(localStorage.getItem("theToken"));
  const [loggedInUser, setLoggedInUser] = useState(defaultUser);
  const [isLoggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const fetchUser = async () => {
      const requestOptions = {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token,
        }
      }

      const response = await fetch("/users/me", requestOptions);

      if (!response.ok) {
        setToken(null);
      }
      localStorage.setItem("theToken", token)
    }
    fetchUser();
  }, [token]);

  const setLoginUserDets = (user) => {
    setLoggedIn(true);
    setLoggedInUser(user);
  };

  const logout = () => {
    setLoggedIn(false);
    setLoggedInUser(defaultUser);
  };

  const refreshLoginDets = async () => {
    let loginDets = {
      userID: loggedInUser.userID,
      password: loggedInUser.password,
    };

    await axios
      .post(API_URL + "login/", loginDets)
      .then((response) => {
        setLoginUserDets(response.data.body);
      })
      .catch((error) => {
        if (error.response) {
          console.log(error.response);
        } else if (error.request) {
          console.log(error.request);
        } else {
          console.log("error", error.message);
        }
      });
  };

  return (
    <div>
      <LoginContext.Provider
        value={{
          loggedInUser,
          isLoggedIn,
          setLoginUserDets,
          logout,
          refreshLoginDets
        }}
      >
        {props.children}
      </LoginContext.Provider>
    </div>
  );
}

export default LoginContext;
