import React, { useEffect, useState, useContext } from "react";
import "./LoginPage.css";
import { Grid, Paper, Avatar, TextField, Button } from "@material-ui/core";
import VpnKeyOutlinedIcon from "@mui/icons-material/VpnKeyOutlined";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Home from "../Home";
import { API_URL } from '../../Constants';
import { LoginContext } from './LoginContext';
import axios from 'axios';

function LoginPage({ props, error }) {

  const {
    loggedInUser,
    isLoggedIn,
    setLoginUserDets,
    logout } = useContext(LoginContext);
  

  useEffect(() => {
    console.log(setLoginUserDets)
    console.log(isLoggedIn)
  }, [])

  const [details, setDetails] = useState({ username: "", password: "" });

  // const [isLoggedIn, setLoggedIn] = useState(false);

  /*const handleSubmit = (e) => {
    e.preventDefault();

    Login(details);
  };*/

  const changeUser = (e) => {
    setDetails.username(e.target.value)
  }

  const changePW = (e) => {
    setDetails.password(e.target.value)
  }

  const handleSubmit = async(e) => {
    e.preventDefault()
    console.log(details.username, " /", details.password)

    let loginDets = {
      userDets: details
    }

    await axios.post(API_URL + "login/", loginDets)
    .then(response => {
      console.log(response);
      setDetails('', '')
      setLoginUserDets(response.data.body)
    }).catch(error => {
      if(error.response) {
        console.log(error.response);
      } else if (error.request) {
          console.log(error.request);
      } else {
          console.log('error', error.message);
      }

      setDetails('', '')
    })
  }

  return (
    <>
      {isLoggedIn ? (
        <Router>
          <Routes>
            <Route path="/" element={<Home />} />
          </Routes>
        </Router>
      ) : (
        <Grid>
          <Paper className="paperStyle" elevation={10}>
            <h2>
              Face Mover&nbsp;&nbsp;
              <i className="fas fa-code-branch fas-fade"></i>
            </h2>
            <Grid className="introSign" align="center">
              <Avatar id="loginLogo">
                <VpnKeyOutlinedIcon />
              </Avatar>
              <h3 className="space">Sign in</h3>
            </Grid>
            <form onSubmit={handleSubmit}>
              {error !== "" ? (
                <div
                  className="error"
                  style={{
                    textAlign: "center",
                    color: "red",
                    marginTop: "20px",
                    marginBottom: "20px",
                  }}
                >
                  {error}
                </div>
              ) : (
                ""
              )}
              <TextField
                id="userField"
                label="Username"
                name="username"
                placeholder="Enter your username"
                variant="standard"
                onChange={changeUser}
                value={details.username}
                fullWidth
                required
              />
              <TextField
                id="passwordField"
                label="Password"
                name="password"
                placeholder="Enter your password"
                variant="standard"
                type="password"
                onChange={changePW}
                value={details.password}
                fullWidth
                required
              />
              {/*<FormControlLabel
              control={
                <Checkbox
                  name="checkedB"
                  color="primary"
                /> 
              }
            label="Remember me"/>*/}
              <Button
                id="submitButton"
                type="submit"
                color="primary"
                variant="contained"
                fullWidth
              >
                Sign In
              </Button>
            </form>
          </Paper>
        </Grid>
      )}
    </>
  );
}

export default LoginPage;
