import React, { useState } from 'react'
import './LoginPage.css'
import { Grid, Paper, Avatar, TextField, Button } from '@material-ui/core'
import VpnKeyOutlinedIcon from '@mui/icons-material/VpnKeyOutlined';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';

const  LoginPage = () => {

  const [ username, setUsername ] = useState("");
  const [ password, setPassword ] = useState("");

  const changeUser = (e) => {
    console.log(e.target)
    const {name, value} = e.target;
    setUsername({...username, [name]: value})
    console.log(username)
  }

  const changePass = (e) => {
    console.log(e.target)
    const {name, value} = e.target;
    setPassword({...password, [name]: value})
    console.log(password)
  }

  const handleSubmit = (e) => {
    e.preventDefault();
  }

  return (
    <Grid>
      <Paper className='paperStyle' elevation={10} onSubmit={handleSubmit}>
        <h2>Face Mover&nbsp;&nbsp;<i className="fas fa-code-branch fas-fade"></i></h2>
        <pre>{JSON.stringify(username, undefined, 1)}</pre>
        <Grid className='introSign' align="center">
          <Avatar id="loginLogo"><VpnKeyOutlinedIcon/></Avatar>
          <h3 className='space'>Sign in</h3>
        </Grid>
        <TextField id="userField" label="Username" placeholder="Enter your username" variant="standard" onChange={ changeUser } fullWidth required/>
        <TextField id="passwordField" label="Password" placeholder="Enter your password" variant="standard" type="password" onChange={ changePass } fullWidth required/>
        {/*<FormControlLabel
            control={
              <Checkbox
                name="checkedB"
                color="primary"
              /> 
            }
          label="Remember me"/>*/}
        <Button id="submitButton" type="submit" color="primary" variant="contained" fullWidth>Sign In</Button>
      </Paper>
    </Grid>
  )
}

export default LoginPage
